library(ggiraph)
library(shiny)
library(tidyverse)
source("R/plotting_functions.R")

server <- function(input, output) {
  data_prs <- reactive({bind_rows(
    tibble(prs = rnorm(input$cases_samples, input$cases_mean, input$cases_sd), group = 'case'),
    tibble(prs = rnorm(input$controls_samples, input$controls_mean, input$controls_sd), group = 'control')) |>
    mutate(z_prs = scale(prs))
  })

  # output$prsHistogram <- renderGirafe({
  #   girafe(code = print(plot_histogram(data_prs())),  width_svg = 6, height_svg = 5.7)
  # })

  output$prsHistogram <- renderPlot({
    plot_histogram(data_prs())
  })

  # output$prsPrevalencebyquantile <- renderGirafe({
  #   girafe(code = print(plot_prevalencebyquantile(data_prs())), width_svg = 4, height_svg = 2.5)
  # })

  output$prsPrevalencebyquantile <- renderPlot({
    plot_prevalencebyquantile(data_prs())
  })

  # output$prsRoc <- renderGirafe({
  #   girafe(ggobj = plot_roc(data_prs()), width_svg = 4, height_svg = 2.5)
  # })

  output$prsRoc <- renderPlot({
    plot_roc(data_prs())
  })

  # output$prsOrs <- renderGirafe({
  #   girafe(ggobj = plot_ors(data_prs()), width_svg = 4, height_svg = 2.5)
  # })

  output$prsOrs <- renderPlot({
    plot_ors(data_prs())
  })

  output$observedMetrics <- renderUI({
    obs_metrics <- observed_metrics(data_prs())

    div(
      class = 'table_cell',
      tagAppendAttributes(class = 'table shiny-table table- spacing-s',tags$table(
        tags$thead(
          tags$tr(
            tagAppendAttributes(rowspan = "2", tags$th("Group")),
            tagAppendAttributes(
              colspan = "2",
              class = "text-center",
              tags$th("Observable")
            ),
            tagAppendAttributes(
              colspan = "2",
              class = "text-center",
              tags$th("Scaled (Z)")
            )
          ),
          tags$tr(
            tags$th("Mean"),
            tags$th("SD"),
            tags$th("Mean"),
            tags$th("SD"),
          )
        ),
        tags$tbody(
          tags$tr(
            tags$td("Case"),
            tags$td(obs_metrics |> filter(Group == 'Case') |> pull(`Obs Mean`) |> round(digits = 2)),
            tags$td(obs_metrics |> filter(Group == 'Case') |> pull(`Obs SD`) |> round(digits = 2)),
            tags$td(obs_metrics |> filter(Group == 'Case') |> pull(`Z-score Mean`) |> round(digits = 2)),
            tags$td(obs_metrics |> filter(Group == 'Case') |> pull(`Z-score SD`) |> round(digits = 2))
          ),
          tags$tr(
            tags$td("Control"),
            tags$td(obs_metrics |> filter(Group == 'Control') |> pull(`Obs Mean`) |> round(digits = 2)),
            tags$td(obs_metrics |> filter(Group == 'Control') |> pull(`Obs SD`) |> round(digits = 2)),
            tags$td(obs_metrics |> filter(Group == 'Control') |> pull(`Z-score Mean`) |> round(digits = 2)),
            tags$td(obs_metrics |> filter(Group == 'Control') |> pull(`Z-score SD`) |> round(digits = 2))
          )
        )
      )
    ))
  })

  output$ttestPvalue <- renderText({
    pvalue <- calc_t_test_pvalue(data_prs())

    color_p_value <-
      if_else(pvalue < 0.01, "#53AD63", "#DB615D")

    str_c(
      "p-value<br><span style='color:",color_p_value,"; font-size:18pt' >",
      format(round(pvalue, 7), nsmall = 7),
      '</span>'
    )
  })

  # output$miniBoxplot <- renderGirafe({
  #   girafe(ggobj = mini_boxplot(data_prs()), width_svg = 4, height_svg = 1.7 )
  # })

  output$miniBoxplot <- renderPlot({
    mini_boxplot(data_prs())
  })

  output$tableOr <- renderTable({
    table_ors(data_prs())
  }, width = '100%', sanitize.text.function = function(x) x)

  output$nagelkerkeR <- renderText({
    r2 <- nagelkerke_r2(data_prs())

    str_c(
      "R2<br><span style='font-size:18pt'>",
      format(round(r2, 7), nsmall = 7),
      '</span>'
    )
  })

  output$orPerSd <- renderText({
    or_per_sd <- or_per_sd(data_prs())
    str_c(
      "OR/SD (scaled)<br><span style='font-size:18pt'>",
      format(round(or_per_sd, 7), nsmall = 7),
      '</span>'
    )
  })
}

// Ensure the DOM is fully loaded before attaching event listeners
document.addEventListener('DOMContentLoaded', function() {
    // Get modal elements
    const openModalButton = document.getElementById('openModalButton');
    const closeModalButton = document.getElementById('closeModalButton');
    const slideshowModal = document.getElementById('slideshowModal');
    const modalBackdrop = document.getElementById('modalBackdrop');

    // Get slideshow elements
    const slides = document.querySelectorAll('.slide-item');
    let currentSlide = 0; // Index of the currently visible slide

    // Function to display a specific slide
    function showSlide(index) {
        // Hide all slides
        slides.forEach(slide => {
            slide.classList.remove('active');
        });

        // Validate index to loop slideshow
        if (index >= slides.length) {
            currentSlide = 0; // Loop to the first slide
        } else if (index < 0) {
            currentSlide = slides.length - 1; // Loop to the last slide
        } else {
            currentSlide = index;
        }

        // Show the target slide
        if (slides[currentSlide]) {
            slides[currentSlide].classList.add('active');
        }
    }

    // Function to change slide (next/prev)
    // Make this function globally accessible if called by inline onclick
    // However, it's better practice to attach event listeners in JS
    window.changeSlide = function(direction) {
        showSlide(currentSlide + direction);
    }

    // Function to open the modal
    function openModal() {
        if (slideshowModal && modalBackdrop) {
            slideshowModal.style.display = 'block';
            modalBackdrop.style.display = 'block';
            document.body.style.overflow = 'hidden'; // Prevent background scrolling
            showSlide(0); // Show the first slide when modal opens
        }
    }

    // Function to close the modal
    function closeModal() {
        if (slideshowModal && modalBackdrop) {
            slideshowModal.style.display = 'none';
            modalBackdrop.style.display = 'none';
            document.body.style.overflow = 'auto'; // Restore background scrolling
        }
    }

    // Event Listeners
    if (openModalButton) {
        openModalButton.addEventListener('click', openModal);
    }
    if (closeModalButton) {
        closeModalButton.addEventListener('click', closeModal);
    }

    // Close modal if backdrop is clicked
    if (modalBackdrop) {
        modalBackdrop.addEventListener('click', closeModal);
    }

    // Close modal with Escape key
    document.addEventListener('keydown', function(event) {
        if (slideshowModal && slideshowModal.style.display === 'block' && event.key === 'Escape') {
            closeModal();
        }
    });

    // Initialize the slideshow by showing the first slide (though it's hidden initially with the modal)
    // This ensures that when the modal becomes visible, the first slide is already set as active.
    if (slides.length > 0) {
        showSlide(currentSlide); // Initialize the first slide
    }

    // If you prefer not to use inline onclick for arrows, you can attach listeners here:
    const prevArrow = document.querySelector('.slideshow-content .prev.nav-arrow');
    const nextArrow = document.querySelector('.slideshow-content .next.nav-arrow');

    if (prevArrow) {
        prevArrow.addEventListener('click', function() {
            changeSlide(-1);
        });
    }
    if (nextArrow) {
        nextArrow.addEventListener('click', function() {
            changeSlide(1);
        });
    }
    // If you attach listeners here, you can remove the onclick="changeSlide(...)" from the HTML/R tags.
    // For simplicity with Shiny tags and direct HTML translation, I kept the onclick in the R code.
    // If you remove them, make sure `changeSlide` is NOT on the window object.
});

